$(document).ready(function(){function a(){var b=0;var c=0;$("#tab_logic tbody tr").each(function(f,m){var m=$(m);var l=m.find("input[name=qty"+f+"]").val();var h=m.find("input[name=price"+f+"]").val();var e=m.find("input[name=dis"+f+"]").val();var j=l*h;var g=(e*h)/100;if(!j){j=0}if(!g){g=0}b+=j;var k=(g*l);var d=j-k;$("#total"+f).html(d.toFixed(2));c+=k});$("#total").html(b.toFixed(2));$("#discount").html(c.toFixed(2));$("#net").html((b-c).toFixed(2));$("#sum").html(b.toFixed(2))}$("#tab_logic input").keyup(function(){a()});a()});